mapper.update:
update pms_user
avatar = #{avatar,jdbcType=VARCHAR}, account = #{account,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, birthday = #{birthday,jdbcType=TIMESTAMP}, sex = #{sex,jdbcType=INTEGER}, email = #{email,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, roleid = #{roleid,jdbcType=VARCHAR}, deptid = #{deptid,jdbcType=INTEGER}, status = #{status,jdbcType=INTEGER},
where id = #{id,jdbcType=INTEGER}

update pms_user
    set password=#{password},salt=#{salt},
    
